{% block title %}Réinitialisation du mot de passe{% endblock %}

{% block body %}
    <div class=\"container d-flex justify-content-center align-items-center\" style=\"min-height: 100vh;\">
        <div class=\"card shadow-lg w-50\">
            <div class=\"card-body\">
                <h5 class=\"card-title text-center mb-4\">Veuillez entrer votre nouveau mot de passe</h5>

                {{ form_start(form, {'attr': {'class': 'form-reset-password'}}) }}
                    <div class=\"mb-3\">
                        {{ form_label(form.password, 'Nouveau mot de passe', {'attr': {'class': 'form-label'}}) }}
                        {{ form_widget(form.password, {'attr': {'class': 'form-control', 'placeholder': 'Entrez votre nouveau mot de passe'}}) }}
                        {{ form_errors(form.password) }}
                    </div>

                    <!-- Ajoutez un champ pour la confirmation du mot de passe -->
                    <div class=\"mb-3\">
                        {{ form_label(form.confirm_password, 'Confirmer le mot de passe', {'attr': {'class': 'form-label'}}) }}
                        {{ form_widget(form.confirm_password, {'attr': {'class': 'form-control', 'placeholder': 'Confirmez votre mot de passe'}}) }}
                        {{ form_errors(form.confirm_password) }}
                    </div>

                    <!-- Bouton de soumission en dessous des champs -->
                    <div class=\"text-center\">
                        <button type=\"submit\" class=\"btn btn-primary w-100\">Réinitialiser le mot de passe</button>
                    </div>
                {{ form_end(form) }}
            </div>
        </div>
    </div>
{% endblock %}
